const request = require('request');

const options = {
  method: 'POST',
  url: 'http://localhost:8002/configs',
  headers: {accept: 'application/json', 'content-type': 'application/json'},
  body: {
    name: 'string',
    active: true,
    frequency: 'string',
    service: 'string',
    source: 'string',
    context: 'string',
    depends_on: [{service: 'string', source: 'string'}],
    service_parameters: {},
    source_parameters: {}
  },
  json: true
};

request(options, function (error, response, body) {
  if (error) throw new Error(error);

  console.log(body);
});

